.C_AXI_ID_WIDTH (%cntInfo_u%memType_uC_S_AXI_ID_WIDTH), .C_AXI_ADDR_WIDTH (%cntInfo_u%memType_uC_S_AXI_ADDR_WIDTH), .C_AXI_DATA_WIDTH (%cntInfo_u%memType_uC_S_AXI_DATA_WIDTH), .C_AXI_NBURST_SUPPORT (%cntInfo_u%memType_uC_AXI_NBURST_TEST), .C_EN_WRAP_TRANS (%cntInfo_u%memType_uC_EN_WRAP_TRANS), .C_BEGIN_ADDRESS (%cntInfo_u%memType_uBEGIN_ADDRESS), .C_END_ADDRESS (%cntInfo_u%memType_uEND_ADDRESS), .DBG_WR_STS_WIDTH (%cntInfo_u%memType_uDBG_WR_STS_WIDTH), .DBG_RD_STS_WIDTH (%cntInfo_u%memType_uDBG_RD_STS_WIDTH), .ENFORCE_RD_WR (%cntInfo_u%memType_uENFORCE_RD_WR), .ENFORCE_RD_WR_CMD (%cntInfo_u%memType_uENFORCE_RD_WR_CMD), .ENFORCE_RD_WR_PATTERN (%cntInfo_u%memType_uENFORCE_RD_WR_PATTERN)
) %cntInfo_laxi4_tg_inst ( .aclk (%cntInfo_l%memType_lclk), .aresetn (%cntInfo_l%memType_laresetn),
// Input control signals .init_cmptd (%cntInfo_l%memType_lphy_init_done), .init_test (1'b0), .wdog_mask (1'b0), .wrap_en (1'b0),
// AXI write address channel signals .axi_wready (%cntInfo_l%memType_ls_axi_awready), .axi_wid (%cntInfo_l%memType_ls_axi_awid), .axi_waddr (%cntInfo_l%memType_ls_axi_awaddr), .axi_wlen (%cntInfo_l%memType_ls_axi_awlen), .axi_wsize (%cntInfo_l%memType_ls_axi_awsize), .axi_wburst (%cntInfo_l%memType_ls_axi_awburst), .axi_wlock (%cntInfo_l%memType_ls_axi_awlock), .axi_wcache (%cntInfo_l%memType_ls_axi_awcache), .axi_wprot (%cntInfo_l%memType_ls_axi_awprot), .axi_wvalid (%cntInfo_l%memType_ls_axi_awvalid),
